import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [facts, setFacts] = useState([]);
  const [filters, setFilters] = useState({ ipr_type: 'All', domain: 'All', sort: 'Newest' });

  // Fetch facts from your local backend
  useEffect(() => {
    fetchFacts();
  }, [fetchFacts]); // Now it depends on the function itself

  const fetchFacts = useCallback(async () => {
    try {
      const params = new URLSearchParams(filters).toString();
      const response = await axios.get(`http://localhost:5000/api/facts?${params}`);
      setFacts(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }, [filters]); // This tells React to only recreate this function if filters change

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  // Function to add sample data to your empty database
  const seedData = async () => {
    try {
      await axios.post('http://localhost:5000/api/facts/seed');
      alert('Sample data added!');
      fetchFacts(); // Refresh the list
    } catch (error) {
      alert('Error adding data');
    }
  };

  return (
    <div className="container">
      <header>
        <h1>IPQuest Fact Explorer</h1>
        <p>Discover Intellectual Property Trends</p>
      </header>

      <div className="controls">
        <select name="ipr_type" onChange={handleFilterChange}>
          <option value="All">All Types</option>
          <option value="Patent">Patent</option>
          <option value="Copyright">Copyright</option>
          <option value="Trademark">Trademark</option>
        </select>
        
        <select name="domain" onChange={handleFilterChange}>
          <option value="All">All Domains</option>
          <option value="Technology">Technology</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Fashion">Fashion</option>
        </select>

        <button className="seed-btn" onClick={seedData}>
           Add Sample Data
        </button>
      </div>

      <div className="grid">
        {facts.map((fact) => (
          <div key={fact._id} className={`card ${fact.ipr_type}`}>
            <span className="badge">{fact.ipr_type}</span>
            <h3>{fact.title}</h3>
            <p>{fact.description}</p>
            <div className="footer">
              <span>{fact.domain}</span>
              <span>{fact.year}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
