import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Admin = () => {
  const [facts, setFacts] = useState([]);

  // Fetch all facts
  const fetchFacts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/facts');
      setFacts(response.data);
    } catch (err) {
      console.error("Error fetching facts:", err);
    }
  };

  useEffect(() => { fetchFacts(); }, []);

  const deleteFact = async (id) => {
    if (window.confirm("‚ö†Ô∏è Are you sure you want to delete this fact permanently?")) {
      await axios.delete(`http://localhost:5000/api/facts/${id}`);
      fetchFacts();
    }
  };

  const fetchFromWeb = async () => {
    try {
        // Show a loading state or alert
        const confirmFetch = window.confirm("Connect to NewsAPI and fetch latest IPR news?");
        if (!confirmFetch) return;

        alert("üîÑ Connecting to News API... Please wait.");
        const res = await axios.post('http://localhost:5000/api/facts/fetch-news');
        alert(res.data.message); 
        fetchFacts(); 
    } catch (err) {
        alert("Failed to fetch news. Check backend console.");
        console.error(err);
    }
  };

  return (
    <div className="container" style={{ borderTop: '5px solid #c0392b' }}>
      <header>
        <h1 style={{ color: '#c0392b' }}>‚ö†Ô∏è Admin Control Panel</h1>
        <p>Restricted Area: Manage Database & Fetch Live News</p>
      </header>

      {/* üü¢ BUTTON GROUP (Styled & Centered) */}
      <div className="button-group">
        
        <Link to="/add">
            <button className="toggle-btn btn-primary">
                <span>‚ûï</span> Manually Add Fact
            </button>
        </Link>
        
        {/* Fetch News Button */}
        <button onClick={fetchFromWeb} className="toggle-btn btn-purple">
            <span>üåç</span> Fetch Live News
        </button>

        {/* Reset DB Button */}
        <button onClick={async () => { 
            if(window.confirm("‚ö†Ô∏è DANGER: This will wipe all current data and reset to defaults. Continue?")) {
                await axios.post('http://localhost:5000/api/facts/seed'); 
                fetchFacts(); 
            }
        }} className="toggle-btn btn-reset">
          <span>üîÑ</span> Reset / Wipe DB
        </button>

      </div>

      <h3 style={{ textAlign: 'center', marginTop: '40px', color: '#6b7280' }}>
        Current Database Content ({facts.length} items)
      </h3>
      
      {/* üü¢ CARD GRID (Matches User View + Delete Button) */}
      <div className="grid">
        {facts.map((fact) => (
          <div key={fact._id} className={`card ${fact.ipr_type ? fact.ipr_type.replace(" ", "") : "General"}`}>
             
             {/* Content */}
             <div className="card-content">
                {/* Header: Badge + Delete Button */}
                <div className="admin-card-header">
                    <span className="badge">{fact.ipr_type}</span>
                    <button 
                        onClick={() => deleteFact(fact._id)} 
                        className="delete-btn"
                        title="Delete this fact"
                    >
                        üóëÔ∏è
                    </button>
                </div>

                <h3>{fact.title}</h3>
                <p>{fact.description}</p>
             </div>

             {/* Footer */}
             <div className="card-footer">
                <div className="footer-item">
                  <span>üìÇ</span> {fact.domain}
                </div>
                <div className="footer-item">
                  <span>üìÖ</span> {fact.year}
                </div>
             </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Admin;
